// This query helps hunt for Teams messages with malicious URLs based on external IOC source
let url = (externaldata(url: string )
[@"https://urlhaus.abuse.ch/downloads/text_online/"]
with (format="txt"))
| project url;
url
| join (MessageUrlInfo
| where Timestamp > ago(14d) 
) on $left.url == $right.Url
|join MessageEvents on TeamsMessageId
//|join UrlClickEvents on Url
|project Timestamp, TeamsMessageId, Url, UrlDomain, SenderEmailAddress, SenderDisplayName, RecipientDetails,ThreatTypes, DetectionMethods, DeliveryAction,IsExternalThread, IsOwnedThread//, AccountUpn, ActionType, AppName, AppVersion, Workload

//This query can be used as a Custom Detection Rule (CDR) to trigger when a partner email domain or email address is used in a Sender display name part of an inbound external Teams message
 MessageEvents 
 | where IsOwnedThread==0 and IsExternalThread==1 and SenderDisplayName contains "@contoso.com"

let lookback = 1d;
// External Teams messages 
let externalMsgs =
  MessageEvents
  | where Timestamp > ago(lookback) and IsExternalThread == true
  | project MsgTime = Timestamp, TeamsMessageId, SenderEmailAddress, ME_ReportId = ReportId;
// URLs found in Teams messages 
let urlsInMsgs =
  MessageUrlInfo
  | where Timestamp > ago(lookback)
  | project MUI_Time = Timestamp, TeamsMessageId, Url, UrlDomain, MUI_ReportId = ReportId;
// Clicks coming from Teams 
let clicks =
  UrlClickEvents
  | where Timestamp > ago(lookback) and Workload == "Teams"
  | project ClickTime = Timestamp, Url, Clicker = AccountUpn, ClickAction = ActionType, UCE_ReportId = ReportId;
// Define "rare" domains in the period 
let rareDomains =
  urlsInMsgs
  | summarize msgCount = dcount(TeamsMessageId) by UrlDomain
  | where msgCount < 3;
rareDomains
  | join kind=inner (urlsInMsgs) on UrlDomain
  | join kind=leftouter (externalMsgs) on TeamsMessageId
  | join kind=leftouter (clicks) on Url
  | project
      Timestamp = coalesce(ClickTime, MUI_Time, MsgTime),
      UrlDomain,
      Url,
      SenderEmailAddress,
      Clicker,
      ClickTime,
      ClickAction,
      TeamsMessageId,
      ReportId = coalesce(UCE_ReportId, MUI_ReportId, ME_ReportId)

//Admin submission of false negative Teams message detections with Malware and Phish threat daily trend
let TimeStart = startofday(ago(30d));
let TimeEnd = startofday(now());
let baseQuery=CloudAppEvents
| extend SubmissionType = tostring((parse_json(RawEventData)).SubmissionType),SubmissionContentType=tostring((parse_json(RawEventData)).SubmissionContentType)
| where ActionType == "AdminSubmissionSubmitted" and SubmissionContentType == "ChatMessage";
let Admin_Malware_FN=baseQuery
| make-series Count= countif(SubmissionType == "2") default = 0 on Timestamp from TimeStart to TimeEnd step 1d
| extend Details = "Admin_Malware_FN";
let Admin_Phish_FN=baseQuery
| make-series Count= countif(SubmissionType == "1") default = 0 on Timestamp from TimeStart to TimeEnd step 1d
| extend Details = "Admin_Phish_FN";
union Admin_Malware_FN,Admin_Phish_FN
| project Count, Details, Timestamp
| render timechart

