let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
// baseQuery _FN_Admin_Submissions_SPM
let _FN_Admin_Submissions_SPM = toscalar(
CloudAppEvents
| where Timestamp between (minTime .. maxTime)
| where ActionType == "AdminSubmissionSubmitted"
| extend SubmissionType = tostring((parse_json(RawEventData)).SubmissionType)
| extend NetworkMessageId = tostring((parse_json(RawEventData)).ObjectId)
| where SubmissionType in ("3")
| summarize count()
);
// baseQuery _FN_ZAP_Successful_Spam
let _FN_ZAP_Successful_Spam = toscalar(
EmailPostDeliveryEvents
| where ActionTrigger == "SpecialAction" and ActionType == "Spam ZAP" and ActionResult == "Success" and Timestamp between (minTime .. maxTime)
| summarize count()
    //Take all our Spam ZAP, and count how many messages we missed and successfully cleaned up.
);
// baseQuery _FN_ZAP_Unsuccessful_Spam
let _FN_ZAP_Unsuccessful_Spam = toscalar(
EmailPostDeliveryEvents
| where ActionTrigger == "SpecialAction" and ActionType == "Spam ZAP" and ActionResult != "Success"
| summarize count()
//Take all our Spam ZAP, and count how many messages we failed to clean up due to error or users/admins/API vendors getting to the message first.
);
// baseQuery _SPAM_ZAP_FP
let _SPAM_ZAP_FP = toscalar(
EmailPostDeliveryEvents
| where ActionTrigger == "SpecialAction" and ActionType == "Redelivery" and Timestamp between (minTime .. maxTime)
| join kind=leftsemi (
            EmailEvents
            | where Timestamp between (minTime ..maxTime )
            | where ThreatTypes contains "Spam"
            | project NetworkMessageId, ThreatTypes)
            on NetworkMessageId
| summarize count()
// count all the FPs which were originally Spam, and when we TT'd them were not so we can subtract that from our mailfow catch, and therefore the total true bad.
);
// baseQuery _Spam_Mailflow
let _Spam_Mailflow = toscalar(
EmailEvents
| where EmailDirection == "Inbound" and ThreatTypes == "Spam" and Timestamp between (minTime .. maxTime)
| extend Key = strcat(NetworkMessageId , '-', RecipientEmailAddress) 
| summarize arg_max(Timestamp, *) by Key
| summarize count()
// Get all the mailflow detected as SPM, then count all the NMIDs (not distinct)
);
print
Effectiveness_PostDelivery = abs(round(((toreal(toscalar(_FN_Admin_Submissions_SPM))+toreal(toscalar(_FN_ZAP_Unsuccessful_Spam)))/(toreal(toscalar(_Spam_Mailflow))+toreal(toscalar(_FN_ZAP_Successful_Spam))+toreal(toscalar(_FN_ZAP_Unsuccessful_Spam))+toreal(toscalar(_FN_Admin_Submissions_SPM))-toreal(toscalar(_SPAM_ZAP_FP)))*100-100),2))","size":1,"showAnalytics":true,"timeContextFromParameter":"TimeRange","queryType":0,"resourceType":"microsoft.operationalinsights/workspaces","crossComponentResources":["{Workspace}"],"visualization":"stat","statSettings":{"valueAggregation":"None","colorSettings":{"type":"thresholds","mode":"foreground","heatmapPalette":"greenRed","thresholdsGrid":[{"operator":">","thresholdValue":"95","representation":"green"},{"operator":"<=","thresholdValue":"95","representation":"orange"},{"operator":"<=","thresholdValue":"90","representation":"red"}]},"numberFormatSettings":{"unit":1,"options":{"style":"decimal"}},"tagText":"Spam Effectiveness (without Edge data)","valueFontStyle":"superLarge","tagTextPosition":"bottom"}},"customWidth":"50","name":"query - 11","styleSettings":{"margin":"10px","padding":"10px"}},{"type":3,"content":{"version":"KqlItem/1.0","query":"let minTime = toscalar(EmailEvents | summarize min(Timestamp));
