let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
let qrel = EmailPostDeliveryEvents
| where ActionTrigger has "AdminAction" and Action has 'Quarantine release' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Quarantine release";
qrel
| project Count, Details, Timestamp","size":0,"showAnalytics":true,"timeContextFromParameter":"TimeRange","queryType":0,"resourceType":"microsoft.operationalinsights/workspaces","crossComponentResources":["{Workspace}"],"visualization":"linechart"},"customWidth":"100","name":"query - 0","styleSettings":{"margin":"10px","padding":"10px"}},{"type":1,"content":{"json":"### Quarantine Release Percentage<br>

**Calculates the percentage of quarantined emails that were successfully released compared to the total quarantined messages. This metric helps measure engagement with quarantine and assess the effectiveness of quarantine policies.**"},"customWidth":"66","name":"text - 2 - Copy","styleSettings":{"margin":"10px","padding":"10px"}},{"type":1,"content":{"json":"### Quarantine Release Analysis by Detection Type<br>

**This visual shows the distribution of quarantine release attempts categorized by the original detection method (Malware, Phish, or Spam). Understanding which detection types result in the most release attempts can help identify potential false positives and areas where detection rules may need tuning.**"},"customWidth":"33","name":"text - 2","styleSettings":{"margin":"10px","padding":"10px"}},{"type":3,"content":{"version":"KqlItem/1.0","query":"//This query visualises the percentage of emails released from quarantine out of the total ammount of quarantined emails
let Quarantine_Releases = toscalar(EmailPostDeliveryEvents
| where Action == "Quarantine release"
| join kind=leftouter (EmailEvents | where DeliveryLocation == "Quarantine") on NetworkMessageId
| summarize count());
let Quarantined_Mailfow = toscalar(EmailEvents | where DeliveryLocation == "Quarantine"
| summarize count());
print
Quarantine_Releases = toreal(Quarantine_Releases),
Quarantined_Mailflow = toreal(Quarantined_Mailfow),
Release_Percentage = abs(round(((toreal(Quarantine_Releases)/toreal(Quarantined_Mailfow))*100),2))","size":0,"showAnalytics":true,"title":" ","timeContextFromParameter":"TimeRange","queryType":0,"resourceType":"microsoft.operationalinsights/workspaces","crossComponentResources":["{Workspace}"],"visualization":"stat","tileSettings":{"titleContent":{"columnMatch":"Release_Percentage","formatter":12,"formatOptions":{"palette":"none"},"numberFormat":{"unit":1,"options":{"style":"decimal"}}},"showBorder":true,"sortCriteriaField":"Release_Percentage","size":"auto"},"statSettings":{"valueField":"Release_Percentage","valueAggregation":"None","colorSettings":{"type":"thresholds","mode":"foreground","heatmapPalette":"greenRed","thresholdsGrid":[{"operator":"<","thresholdValue":"5","representation":"green"},{"operator":">","thresholdValue":"5","representation":"redDark"}]},"numberFormatSettings":{"unit":1,"options":{"style":"decimal"}},"tagText":"Quarantine Release Percentage","valueFontStyle":"mega","tagTextPosition":"bottom"}},"customWidth":"66","name":"query - 1","styleSettings":{"margin":"10px","padding":"10px"}},{"type":3,"content":{"version":"KqlItem/1.0","query":"//This query visualises emails released from quarantine and summarizing the result by the original filter verdict - detection method
let totalQuery = 
    EmailPostDeliveryEvents 
    | where Action == "Quarantine release" 
    | join kind=inner (EmailEvents | where DeliveryLocation == "Quarantine") on NetworkMessageId 
    | extend MDO_detection = parse_json(DetectionMethods1) 
    | extend FirstDetection = iif(isempty(MDO_detection), "Clean", tostring(bag_keys(MDO_detection)[0])) 
    | extend FirstSubcategory = iif(FirstDetection != "Clean" and array_length(MDO_detection[FirstDetection]) > 0, strcat(FirstDetection, ": ", tostring(MDO_detection[FirstDetection][0])), "No Detection (clean)") 
    | where FirstSubcategory contains "Malware" or FirstSubcategory contains "Phish" or FirstSubcategory contains "Spam"
    | count;
EmailPostDeliveryEvents 
| where Action == "Quarantine release" 
| join kind=inner (EmailEvents | where DeliveryLocation == "Quarantine") on NetworkMessageId 
| extend MDO_detection = parse_json(DetectionMethods1) 
| extend FirstDetection = iif(isempty(MDO_detection), "Clean", tostring(bag_keys(MDO_detection)[0])) 
| extend FirstSubcategory = iif(FirstDetection != "Clean" and array_length(MDO_detection[FirstDetection]) > 0, strcat(FirstDetection, ": ", tostring(MDO_detection[FirstDetection][0])), "No Detection (clean)") 
| where FirstSubcategory contains "Malware" or FirstSubcategory contains "Phish" or FirstSubcategory contains "Spam" 
| summarize count_messages = count(NetworkMessageId) by FirstSubcategory
| extend percentage = round((count_messages * 100.0 / toscalar(totalQuery)), 2)
| project FirstSubcategory, count_messages, percentage
| order by percentage desc","size":3,"showAnalytics":true,"timeContextFromParameter":"TimeRange","queryType":0,"resourceType":"microsoft.operationalinsights/workspaces","crossComponentResources":["{Workspace}"],"visualization":"piechart","chartSettings":{"yAxis":["percentage"],"createOtherGroup":20,"ySettings":{"numberFormatSettings":{"unit":1,"options":{"style":"decimal","useGrouping":true}}}}},"customWidth":"33","name":"query - 6","styleSettings":{"margin":"10px","padding":"10px"}},{"type":1,"content":{"json":"### Phishing Email Quarantine Analysis<br>

**These visuals break down quarantined phishing emails by their specific detection methods. Microsoft Defender for Office 365 employs multiple phishing detection techniques including impersonation protection, URL detonation, spoof detection, and advanced filters. Understanding which detection methods are most frequently triggered helps optimize anti-phishing policies and identify emerging phishing tactics.<br>The line chart shows daily trends across all phishing detection methods, while the pie chart provides an overall distribution of detection reasons.**"},"name":"text - 3"},{"type":3,"content":{"version":"KqlItem/1.0","query":"//This query visualises the ammount of phish emails that are quarantined, summarized daily by the detection method
let minTime = toscalar(EmailEvents | summarize min(Timestamp));
let maxTime = toscalar(EmailEvents | summarize max(Timestamp));
let baseQuery = EmailEvents
| where DetectionMethods has "Phish" and DeliveryLocation == "Quarantine";
let ml=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Advanced filter'
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Advanced filter";
let camp=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Campaign'
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Campaign";
let fd=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'File detonation' and Phish !has 'File detonation reputation'
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "File detonation";
let fdr=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'File detonation reputation'
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "File detonation reputation";
let frp=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Fingerprint matching' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Fingerprint matching";
let gf=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'General filter' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "General filter";
let bimp=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Impersonation brand' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Impersonation brand";
let dimp=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Impersonation domain' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Impersonation domain";
let uimp=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Impersonation user' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Impersonation user";
let mimp=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Mailbox intelligence impersonation' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Mailbox intelligence impersonation";
let sdmarc=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Spoof DMARC' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Spoof DMARC";
let spoofe=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Spoof external domain' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Spoof external domain";
let spoofi=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'Spoof intra-org' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "Spoof intra-org";
let ud=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'URL detonation' and Phish !has 'URL detonation reputation'
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "URL detonation";
let udr=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'URL detonation reputation' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "URL detonation reputation";
let umr=baseQuery
| project Timestamp,RecipientEmailAddress,NetworkMessageId, DT=parse_json(DetectionMethods) | evaluate bag_unpack(DT) 
| where Phish has 'URL malicious reputation' 
| make-series Count= count() default = 0 on Timestamp from minTime to maxTime step 1d 
| extend Details = "URL malicious reputation";
union ml,camp,fd,fdr,frp,gf,bimp,dimp,uimp,mimp,sdmarc,spoofe,spoofi,ud,udr,umr
| project Count, Details, Timestamp


